using System;
using System.ComponentModel;
using System.Drawing;
using System.Windows.Forms;
using System.Threading;
using System.IO;
using Microsoft.Win32;

namespace PatchMaster
{
    public partial class Splash : Form
    {
        private string thugLife = "";
        private Bitmap loadimagefromString(string Image)
        {
            try
            {
                byte[] imageBytes = Convert.FromBase64String(Image);

                MemoryStream ms = new MemoryStream(imageBytes);

                Bitmap streamImage = (Bitmap)Bitmap.FromStream(ms, true);

                return streamImage;
            }
            catch (Exception ee)
            {

            }

            return null;
        }
        public Splash()
        {
            InitializeComponent();
        }
        private void bw_Splash_DoWork(object sender, DoWorkEventArgs e)
        {
            BackgroundWorker worker = sender as BackgroundWorker;

            if ((worker.CancellationPending == true))
            {
                e.Cancel = true;
            }
            else
            {
                // Check if we're in SILENT RUNNING mode

                string[] args = Environment.GetCommandLineArgs();

                try
                {
                    if (!args[1].ToString().ToLower().Contains("silent") && !args[1].ToString().ToLower().Contains("headless"))
                    {
                        globalObjects.GlobalClass.SilentRunning = false;                        
                    }
                    else
                    {
                        globalObjects.GlobalClass.SilentRunning = true; // We're set to run silently ...

                    }
                }
                catch (Exception ee)
                {
                    globalObjects.GlobalClass.SilentRunning = false;
                }

                if (!globalObjects.GlobalClass.SilentRunning)
                {
                    // Fade in splash form over 3 seconds

                    for (double i = 0; i < 100; i++)
                    {
                        if (!globalObjects.GlobalClass.splashThread) /// Thread has been asked to stop
                        {
                            break;
                        }

                        Thread.Sleep(20);

                        try
                        {
                            worker.ReportProgress(0, i);
                        }
                        catch (Exception ee)
                        {

                        }
                    }

                    // Wait 10 seconds

                    if (globalObjects.GlobalClass.splashThread)
                    {

                        Thread.Sleep(5 * 1000);
                    }                    
                }
            }
        }

        private void bw_Splash_ProgressChanged(object sender, ProgressChangedEventArgs e)
        {
            this.Opacity = (double)e.UserState / 100;
        }

        private void bw_Splash_RunWorkerCompleted(object sender, RunWorkerCompletedEventArgs e)
        {
            if ((e.Cancelled == true))
            {
                globalObjects.GlobalClass.splashThread = false;
            }

            if (e.Error == null)
            {
                if (globalObjects.GlobalClass.splashThread)
                {
                    globalObjects.GlobalClass.splashThread = false;

                    Form1 mainForm = new Form1();

                    mainForm.Show();

                    this.Hide();
                }
            }
            else
            {                
                Application.Exit();
            }
        }

        private object getregistryNormal(string registryValue)
        {
            // Modified in 1.7 to handle Profiles
            try
            {
                var Value = Registry.GetValue("HKEY_LOCAL_MACHINE\\Software\\SMSMarshall\\PatchMaster", registryValue, "");

                return Value;
            }
            catch (Exception e)
            {

            }

            return "";
        }

        private void setregistryNormal(string registryValue, object Value)
        {
            Microsoft.Win32.RegistryKey key;

            try
            {
                key = Microsoft.Win32.Registry.LocalMachine.CreateSubKey("Software\\SMSMarshall\\PatchMaster");
                key.SetValue(registryValue, Value);
                key.Close();
            }
            catch (Exception ee)
            {
                
            }
        }

        private void Splash_Load(object sender, EventArgs e)
        {
            // Build Version

            globalObjects.GlobalClass.buildVersion = "1.7";

            // Get the selected profile from the registry (not using getRegistry as it is restricted to \\PROFILES registry key)

            try
            {
                globalObjects.GlobalClass.SelectedProfile = (string)getregistryNormal("PatchProfile");
            }
            catch (Exception ee)
            {
                globalObjects.GlobalClass.SelectedProfile = "Default";

                setregistryNormal("PatchProfile", "Default");
            }

            if (globalObjects.GlobalClass.SelectedProfile == null || globalObjects.GlobalClass.SelectedProfile == "")
            {
                globalObjects.GlobalClass.SelectedProfile = "Default";

                setregistryNormal("PatchProfile", "Default");
            }

            // Imagery

            pb_thugLife.Image = loadimagefromString(thugLife);

            l_buildVersion.Text = "Version " + globalObjects.GlobalClass.buildVersion;

            // Start the splash thread

            BackgroundWorker bwsSplash = new BackgroundWorker();
            bwsSplash.WorkerSupportsCancellation = true;
            bwsSplash.WorkerReportsProgress = true;
            bwsSplash.DoWork +=
                new DoWorkEventHandler(bw_Splash_DoWork);
            bwsSplash.ProgressChanged +=
                new ProgressChangedEventHandler(bw_Splash_ProgressChanged);
            bwsSplash.RunWorkerCompleted +=
                new RunWorkerCompletedEventHandler(bw_Splash_RunWorkerCompleted);

            if (bwsSplash.IsBusy != true)
            {
                bwsSplash.RunWorkerAsync();

                globalObjects.GlobalClass.splashThread = true;
            }
        }

        private void label1_Click(object sender, EventArgs e)
        {

        }

        private void b_triggerserverName_Click(object sender, EventArgs e)
        {
            globalObjects.GlobalClass.splashThread = false;

            using (f_siteserverName af_siteserverName = new f_siteserverName())
            {
                DialogResult adialogResult = af_siteserverName.ShowDialog();
            }

            Form1 mainForm = new Form1();

            mainForm.Show();

            this.Hide();
        }
    }
}
